package loops;

public class Pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=5;i>=1;i--)  
		{
			for(int j=5;j>=1;j--)
			{
				System.out.print(i); // 55555,44444,33333,22222,11111
				//System.out.print(j)  //54321,54321,54321,54321,54321
			}
			System.out.println();
		}  

			System.out.println("--------------------------------");
			
			
			  
	}

}
